package DoWhile;


import java.util.*;

public class Navneet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	 Scanner sc = new Scanner(System.in);
	 System.out.println("Enter the number:");
	 int k = sc.nextInt();
	 
	 do {
		System.out.println(k + ".Navneet"); 
		 k++;
	 }while(k<11);
		

	}

}
