package DoWhile;

import java.util.*;

public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   Scanner sc = new Scanner(System.in);
   System.out.println("Enter the number:");
   int n = sc.nextInt();
   
   do {
	   System.out.println(n + ".Hi");
	   n++;
	    }while(n<15);
   
   
   
	}

}
