package WhileLoop;

import java.util.*;

public class Ok {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    Scanner sc = new Scanner(System.in);
    System.out.println("Enter the number:");
    int n = sc.nextInt();
    
    while(n<=10) {
    	System.out.println(n + ".ok");
    	n++;
    }
		
		
	}

}
